import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import './Portfolio.css';

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [activeShowcase, setActiveShowcase] = useState(null);

  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: 'smooth' });

  const showcaseDetails = {
    1: {
      title: 'Technical Report (ENG2003)',
      body: `I chose this report because it was my first major opportunity to demonstrate clear and precise technical communication. It involved describing a complex system to an audience with limited background knowledge, which forced me to rethink my explanations and prioritize clarity. This assignment highlighted my growth in using visuals and structured formatting to support understanding. It definitely pushed me out of my comfort zone—I’m used to writing for peers who understand the context, not general stakeholders. I consider this one of my best pieces because it reflects my ability to balance detail and simplicity without compromising technical integrity.`
    },
    2: {
      title: 'Infographic Presentation (ENG2003)',
      body: `This project stands out because it challenged me to communicate through design as well as words. I selected it for the showcase because it was a turning point in my ability to distill complex data into something visually engaging and accessible. The process of choosing what to include and what to simplify taught me a lot about prioritizing information. It was slightly outside of my comfort zone—I’ve always preferred writing over visuals—but I’m proud of how it turned out. I believe this piece best demonstrates my versatility as a communicator.`
    },
    3: {
      title: 'Email and Memo Writing (ENG2003)',
      body: `I included this piece because it demonstrates my development in day-to-day professional communication. These formats are often overlooked in engineering, but they’re crucial in workplace settings. Writing effective emails and memos helped me understand tone, structure, and how to get to the point without sacrificing completeness. While this assignment felt within my comfort zone, it made me more conscious of the nuances of audience engagement. It may not be my most complex work, but it is some of my most applicable.`
    },
    4: {
      title: 'Hackathon Pitch (External – UNHack 2023)',
      body: `This pitch represents my communication growth outside the classroom. During a 48-hour hackathon, I had to collaborate with a team to build and present a solution addressing a UN sustainability goal. I was responsible for presenting our work to judges and other participants, under time pressure and without a script. It was a challenge, but one that taught me to prioritize clarity and confidence under pressure. I selected this piece because it mirrors real-world engineering communication—concise, persuasive, and impactful. It’s among my best work because it was practical, fast-paced, and very well-received.`
    }
  };

  return (
    <div className="relative min-h-screen text-primary font-sans">
      {/* Background */}
      <div className="fixed inset-0 -z-20">
        <div className="w-full h-full bg-parallax bg-cover bg-center animate-backgroundScroll" />
        <div className="absolute inset-0 bg-gradient-to-b from-black/60 via-black/70 to-black" />
      </div>

      <main className="relative z-10 max-w-5xl mx-auto px-6 py-12 space-y-16">
        <header className="sticky top-4 bg-background/70 backdrop-blur-md rounded-xl px-6 py-4 shadow-md flex justify-between items-center">
          <h1 className="text-2xl font-bold">Sidney Wood</h1>
          <div className="text-sm text-gray-400 text-right">
            <p><a href="mailto:Sidney.scott.wood@gmail.com" className="text-accent underline">Sidney.scott.wood@gmail.com</a></p>
            <p>📍 Toronto, ON · 📞 (647)-542-3540</p>
            <a href="https://www.linkedin.com/in/sidneywood" className="text-accent underline">Visit my LinkedIn</a>
          </div>
        </header>

        {/* 🎓 Showcase Section */}
        <section>
          <h2 className="text-2xl font-semibold mb-4">Showcase</h2>
          <div className="grid md:grid-cols-2 gap-6 text-gray-200">
            {Object.entries(showcaseDetails).map(([key, item]) => (
              <div key={key} className="bg-surface p-6 rounded-xl shadow cursor-pointer hover:shadow-lg transition" onClick={() => setActiveShowcase(key)}>
                <h3 className="text-xl font-semibold">{item.title}</h3>
                <p className="text-gray-400 text-sm mt-2">Click to read more</p>
              </div>
            ))}
          </div>
        </section>

        {/* 🪞 Reflection Link */}
        <section>
          <h2 className="text-2xl font-semibold mb-4">Reflection</h2>
          <a href="/reflection" className="text-accent underline">Read Full Reflection</a>
        </section>
      </main>



      {showScrollTop && (
        <motion.button onClick={scrollToTop} initial={{ opacity: 0, y: 40 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 40 }} transition={{ duration: 0.3 }} className="fixed bottom-6 right-6 z-50 bg-accent text-black px-4 py-2 rounded-full shadow-lg hover:shadow-xl hover:scale-105 transition-all">
          ↑
        </motion.button>
      )}

      <AnimatePresence>
        {activeShowcase && (
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-50 bg-black/70 flex justify-center items-center">
            <div className="bg-surface max-w-lg p-8 rounded-xl shadow-xl relative text-gray-200">
              <h3 className="text-2xl font-semibold mb-4">{showcaseDetails[activeShowcase].title}</h3>
              <p className="mb-6 whitespace-pre-line">{showcaseDetails[activeShowcase].body}</p>
              <button onClick={() => setActiveShowcase(null)} className="absolute top-3 right-3 bg-accent text-black px-4 py-1 rounded hover:scale-105">Close</button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
