import { motion, AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);

  useEffect(() => {
    const onScroll = () => setShowScrollTop(window.scrollY > 300);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="min-h-screen bg-dark-radial text-primary font-sans overflow-x-hidden">
      <main className="max-w-5xl mx-auto px-6 py-12 space-y-16">
      <motion.header
  className="text-center space-y-4 sticky top-0 bg-background/60 backdrop-blur-md z-10 py-6"
        initial={{ opacity: 0, y: -20 }}
  animate={{ opacity: 1, y: 0 }}
  transition={{ duration: 0.6 }}
>

          <h1 className="text-4xl md:text-5xl font-bold">Sidney Wood</h1>
          <p className="text-lg md:text-xl text-gray-400">Computer Engineering Student ¬∑ Developer ¬∑ Robotics Enthusiast</p>
          <div className="text-sm md:text-base text-gray-500">
            <p>Email: <a className="text-accent underline" href="mailto:Sidney.scott.wood@gmail.com">Sidney.scott.wood@gmail.com</a></p>
            <p>üìç Toronto, ON ¬∑ üìû (647)-542-3540</p>
          </div>
        </motion.header>

        {/* Your sections remain unchanged (About, Projects, Skills, Education, etc.) */}
        {/* Include your existing sections below this line ‚Äì I've trimmed them for brevity */}
        {/* ... */}

      </main>

      {/* Scroll to Top Button */}
      <AnimatePresence>
        {showScrollTop && (
          <motion.button
            onClick={scrollToTop}
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 40 }}
            transition={{ duration: 0.3 }}
            className="fixed bottom-6 right-6 bg-accent text-black px-4 py-2 rounded-full shadow-md hover:shadow-lg hover:scale-105 transition"
          >
            ‚Üë Top
          </motion.button>
        )}
      </AnimatePresence>
    </div>
  );
}
