import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import './Portfolio.css'; // Background scroll animation styles

export default function Portfolio() {
  const [showScrollTop, setShowScrollTop] = useState(false);
  const [activeModal, setActiveModal] = useState(null);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      setShowScrollTop(window.scrollY > 300);
      setScrolled(window.scrollY > 100);
    };
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: 'smooth' });

  return (
    <div className="relative min-h-screen text-primary font-sans overflow-x-hidden bg-black">
      <div className="fixed top-0 left-0 w-full h-full bg-parallax bg-cover bg-center animate-backgroundScroll -z-20"></div>
      <div className="absolute inset-0 bg-gradient-to-b from-black/70 to-black/90 -z-10"></div>

      <main className="relative z-10 max-w-5xl mx-auto px-6 py-12 space-y-16">
        <motion.header
          className={`transition-all duration-300 sticky top-0 z-20 backdrop-blur-md bg-black/60 border-b border-gray-800 px-4 py-${scrolled ? '2' : '6'} flex flex-col md:flex-row md:items-center md:justify-between space-y-2 md:space-y-0`}
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1 className="text-xl md:text-2xl font-bold">Sidney Wood</h1>
          <div className="text-sm md:text-base text-gray-400 flex flex-col md:flex-row md:gap-4 items-start md:items-center">
            <a className="text-accent underline" href="mailto:Sidney.scott.wood@gmail.com">Sidney.scott.wood@gmail.com</a>
            <span>üìç Toronto, ON</span>
            <span>üìû (647)-542-3540</span>
          </div>
        </motion.header>

        {/* Your sections here (About, Projects, Skills, etc.) */}

        <AnimatePresence>
          {showScrollTop && (
            <motion.button
              onClick={scrollToTop}
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 40 }}
              transition={{ duration: 0.3 }}
              className="fixed bottom-6 right-6 bg-accent text-black px-4 py-2 rounded-full shadow-md hover:shadow-lg hover:scale-105 transition"
            >
              ‚Üë Top
            </motion.button>
          )}
        </AnimatePresence>

        <AnimatePresence>
          {activeModal === 'mtg' && (
            <ProjectModal
              title="MTG Win Tracker"
              description="A full-stack app for tracking Magic Commander wins with Elo ranking, Flask backend, MySQL database, and Raspberry Pi deployment."
              onClose={() => setActiveModal(null)}
            />
          )}
          {activeModal === 'spiri' && (
            <ProjectModal
              title="Spiri Robotics"
              description="Tools and visualizations for maritime tracking using sound analysis and AIS data, with drone interface integration and Gazebo simulation."
              onClose={() => setActiveModal(null)}
            />
          )}
        </AnimatePresence>
      </main>
    </div>
  );
}

function ProjectModal({ title, description, onClose }) {
  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      exit={{ opacity: 0, scale: 0.95 }}
      className="fixed inset-0 bg-black/60 flex justify-center items-center z-50"
      onClick={onClose}
    >
      <div onClick={(e) => e.stopPropagation()} className="bg-surface p-8 rounded-xl shadow-lg max-w-md w-full">
        <h3 className="text-2xl font-semibold mb-4">{title}</h3>
        <p className="text-gray-300 mb-6">{description}</p>
        <button onClick={onClose} className="bg-accent text-black px-4 py-2 rounded hover:scale-105 transition">
          Close
        </button>
      </div>
    </motion.div>
  );
}
